import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
// import Button from 'react-bootstrap/Button'

import styled from 'styled-components'
import Item from '../components/Item'
// import 'react-table/react-table.css'
import '../css/landing.css'
import { FcEmptyTrash, FcPlus, FcMinus } from 'react-icons/fc';

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class MoviesList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            categories: [],
            columns: [],
            isLoading: false,
            items: [],
            total: 0,
            subTotal: 0,
            displayItems: [],
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMovies().then(movies => {
            console.log(movies);
            this.setState({
                movies: movies.data.data,
                displayItems:  movies.data.data,
                isLoading: false,
            })
        })

        await api.getAllCategories().then(categories => {
            console.log(categories);
            this.setState({
                categories: categories.data.data
            })
        })
    }

    addItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        console.log(itemUnitPrice);

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal + itemUnitPrice,
        })
    }

    removeItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity-1, total: (obj.quantity-1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })
    }

    deleteItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        let newState = (this.state.items).filter((item) => item.name !== id);
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })

        // console.log((this.state.items).sum('total'));
    }

    handleClick(id, imageName, index) {
        // this.setState(prevState => ({
        //     isToggleOn: !prevState.isToggleOn
        // }));
        console.log(this.state.items);
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        // console.log((this.state.items).find(x => x.name === id))
        if ((this.state.items).find(x => x.name === id)) {
            console.log((this.state.items).find(x => x.name === id));

            let newState = (this.state.items).map(obj =>
                obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice, image: imageName, id: index } : obj
            );

            console.log(newState)

            this.setState({
                items: newState
            })




        } else {
            console.log('aaaaaa');
            console.log(this.state.items);

            this.setState({
                items: [
                  ...this.state.items,
                  {'name': id, 'quantity' : 1, 'total': itemUnitPrice, 'image': imageName, 'id': index} 
                ]
              })



            console.log(this.state.subTotal);
        }

        this.setState({
            subTotal: this.state.subTotal + itemUnitPrice,
        })
        console.log('bbbbbbbbbbbbbbbbbbb');
        console.log(this.state.items);


    }

    addOrder() {
        console.log('orderrrrrrrrrrrr');
        console.log(this.state.items)
        let result = (this.state.items).map(e => ({ id: e.id, quantity: e.quantity }));
        console.log(result);

        //insertMovie
        api.insertMovie({
            "subTotal" : this.state.subTotal,
            "tax" : this.state.subTotal*0.05,
            "total" : this.state.subTotal + this.state.subTotal*0.05,
            "discount" : 200,
            "items" : result,
        }).then(order2 => {
            console.log("aaaadd");
            console.log(order2)

        })
    }



    changeCategory(category) {
        console.log(category)
        if (category == 'all') {
            this.setState({
                displayItems:  this.state.movies,
            })
        } else {
            let filteredItems = (this.state.movies).filter(obj => obj.category[0].name === category);
            console.log(filteredItems);
            this.setState({
                displayItems:  filteredItems,
            })
        }

    }

    clearOrder() {
        this.setState({
            items: [],
            total: 40,
            subTotal: 0,
        })
    }

    render() {
        const { movies, isLoading, items, displayItems, categories } = this.state
        console.log('TCL: MoviesList -> render -> movies', movies)



        let showTable = true
        if (!movies.length) {
            showTable = false
        }

        return (
            <Wrapper>               

                <div className="main-div">                    
                    <div style={{height: "100%", width: "60%", flexDirection: "column", flexWrap: "wrap", display: "flex", justifyContent: "space-between"}} className="users">


                    <div>
                        <div>
                            <h2>Simonss's BBQ Team</h2>
                            <h5>Simonss's BBQ Team</h5>
                        </div>
                        <div className="category-section">
                            <button className="category-btn" onClick={() => this.changeCategory('all')}>All Items</button>
                            {categories.map((category) => (
                                <button className="category-btn" onClick={() => this.changeCategory(category.name)}>{category.name}</button>
                            ))}

                        </div>
                    </div>



                        <div style={{flexDirection: "row", flexWrap: "wrap", display: "flex", justifyContent: "space-between"}}>
                        {displayItems.map((movie) => (

                                <div onClick={() => this.handleClick(movie.name, movie.image, movie._id)} style={{height:"140px", borderStyle: "solid", borderColor: "#d5cccc", minWidth: "300px", borderRadius: '19px', margin: "10px", padding: "20px"}}>
                                    <Item itemData={{ name: movie.name, price: movie.price, image: movie.image}} />

                                    {/* <div>
                                        <div>{movie.name}</div>
                                        <div style={{display: "flex", justifyContent: "space-between"}}>
                                            <div style={{display: "flex", justifyContent: "space-between", flexDirection: "column"}}>
                                                <div>aaaa</div>
                                                <div style={{color: "orange"}}>{movie.price}</div>
                                            </div>
                                            <div>
                                                <img style={{height: "60px"}} src="https://previews.123rf.com/images/aquir/aquir1311/aquir131100316/23569861-sample-grunge-red-round-stamp.jpg" />
                                            </div>
                                        </div>
                                    </div> */}
                                </div>

                        ))}
                        </div>

                    </div>

                    <div style={{width: "40%", padding: "40px", margin: "19px", borderStyle: "solid", borderColor: "#d5cccc"}}>
                        <div>
                            <span>Current Order</span>
                            <button style={{width: "83px", border: "none", background: "#d5cccc", borderRadius: "12px", color: "white", padding: "3px", marginLeft: "4%"}} onClick={() => this.clearOrder()}>Clear All</button>

                            <table style={{width: "100%", marginTop: "10px"}}>
                                {items.map((item) => (
                                    <tr>
                                        <td><img className="list-image" src={"/images/"+item.image} /></td>
                                        <td>{item.name}</td>
                                        <td>
                                            {item.quantity > 1 ? <div onClick={() => this.removeItem(item.name)}><FcMinus /></div>
                                            : <div onClick={() => this.deleteItem(item.name)}><FcEmptyTrash /></div> }
                                        </td>
                                        <td>{item.quantity}</td>
                                        <td><div onClick={() => this.addItem(item.name)}><FcPlus /></div></td>
                                        <td style={{textAlign: "right"}}>${item.total.toFixed(2)}</td>
                                    </tr>
                                ))}
                            </table>


