import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
// import Button from 'react-bootstrap/Button'

import styled from 'styled-components'

// import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class MoviesList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            columns: [],
            isLoading: false,
            items: [],
            total: 40,
            subTotal: 0,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMovies().then(movies => {
            console.log(movies);
            this.setState({
                movies: movies.data.data,
                isLoading: false,
            })
        })
    }

    addItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        console.log(itemUnitPrice);

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal + itemUnitPrice,
        })
    }

    removeItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity-1, total: (obj.quantity-1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })
    }

    deleteItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        let newState = (this.state.items).filter((item) => item.name !== id);
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })

        // console.log((this.state.items).sum('total'));
    }

    handleClick(id) {
        // this.setState(prevState => ({
        //     isToggleOn: !prevState.isToggleOn
        // }));
        console.log(this.state.items);
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        // console.log((this.state.items).find(x => x.name === id))
        if ((this.state.items).find(x => x.name === id)) {
            console.log((this.state.items).find(x => x.name === id));

            let newState = (this.state.items).map(obj =>
                obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice } : obj
            );

            console.log(newState)

            this.setState({
                items: newState
            })




        } else {
            console.log('aaaaaa');
            console.log(this.state.items);

            this.setState({
                items: [
                  ...this.state.items,
                  {'name': id, 'quantity' : 1, total: itemUnitPrice} 
                ]
              })


            // this.setState({
            //     items: (this.state.items).push({'name': id, 'quantity' : 1})
            // })
            console.log(this.state.items);
        }

        console.log('bbbbbbbbbbbbbbbbbbb');
        console.log(this.state.items);


    }

    addOrder() {
        console.log('orderrrrrrrrrrrr');
        //insertMovie
        api.insertMovie({"name" : "Chillie2",
        "time" : [ 
            "15:00", 
            "20:00"
        ],
        "total" : this.state.total}).then(order2 => {
            console.log("aaaadd");
            console.log(order2)

        })
    }

    render() {
        const { movies, isLoading, items } = this.state
        console.log('TCL: MoviesList -> render -> movies', movies)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Rating',
                accessor: 'rating',
                filterable: true,
            },
            {
                Header: 'Time',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
        ]

        let showTable = true
        if (!movies.length) {
            showTable = false
        }

        return (
            <Wrapper>
                <div style={{display: "flex"}}>


                    <div style={{width: "60%", flexWrap: "wrap", display: "flex", justifyContent: "space-between"}} className="users">

                        {movies.map((movie) => (

                                <div onClick={() => this.handleClick(movie.name)} style={{borderStyle: "solid", borderColor: "#d5cccc", minWidth: "300px", borderRadius: '19px', padding: "20px"}}>
                                    <div>{movie.name}</div>
                                    <div style={{display: "flex", justifyContent: "space-between"}}>
                                        <div style={{display: "flex", justifyContent: "space-between", flexDirection: "column"}}>
                                            <div>aaaa</div>
                                            <div style={{color: "orange"}}>{movie.price}</div>
                                        </div>
                                        <div>
                                            <img style={{height: "60px"}} src="https://previews.123rf.com/images/aquir/aquir1311/aquir131100316/23569861-sample-grunge-red-round-stamp.jpg" />
                                        </div>
                                    </div>
                                </div>

                        ))}

                    </div>

                    <div style={{width: "40%", padding: "40px", margin: "19px", borderStyle: "solid", borderColor: "#d5cccc"}}>
                        <div>

                            {items.map((item) => (
                                    <div style={{display: "flex", justifyContent: "space-between"}}>
                                    <div><img style={{height: "20px"}} src="https://previews.123rf.com/images/aquir/aquir1311/aquir131100316/23569861-sample-grunge-red-round-stamp.jpg" /></div>
                                    <div>{item.name}</div>
                                    <div onClick={() => this.addItem(item.name)}>+</div>
                                    <div>{item.quantity}</div>




                                    {item.quantity > 1 ? <div onClick={() => this.removeItem(item.name)}>-</div>
                                        : <div onClick={() => this.deleteItem(item.name)}>---</div>      }



                                    <div>
                                        {console.log('ttttttttttttt')}
                                        {item.total}

                                    </div>
                                </div>
                            ))}




                        </div>
                        <div style={{display: "flex", flexDirection: "column"}}>
                            <div style={{display: "flex", justifyContent: "space-between"}}>
                                <div>Sub Total</div>
                                <div>85</div>
                            </div>
                            <div style={{display: "flex", justifyContent: "space-between"}}>
                                <div>Discount</div>
                                <div>85</div>
                            </div>
                            <div style={{display: "flex", justifyContent: "space-between"}}>
                                <div>Sub Total</div>
                                <div>85</div>
                            </div>
                            <div style={{display: "flex", justifyContent: "space-between"}}>
                                <div>Sub Total</div>
                                <div>85</div>
                            </div>
                        </div>
                        <button onClick={() => this.addOrder()}>Pay</button>
                    </div>
                </div>
                {/* {showTable && (
                    <ReactTable
                        data={movies}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )} */}
            </Wrapper>
        )
    }
}

export default MoviesList
