import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'

import styled from 'styled-components'

// import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class MoviesList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMovies().then(movies => {
            console.log(movies);
            this.setState({
                movies: movies.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { movies, isLoading } = this.state
        console.log('TCL: MoviesList -> render -> movies', movies)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Rating',
                accessor: 'rating',
                filterable: true,
            },
            {
                Header: 'Time',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
        ]

        let showTable = true
        if (!movies.length) {
            showTable = false
        }

        return (
            <Wrapper>
                <div tyle={{display: "flex"}}>


                <div style={{display: "flex", justifyContent: "space-between"}} className="users">

                    {movies.map((movie) => (

                    <div style={{borderStyle: "solid", borderColor: "#d5cccc", minWidth: "15%", borderRadius: '19px', padding: "20px"}}>
                        <div>{movie.name}</div>
                        <div style={{display: "flex", justifyContent: "space-between"}}>
                            <div style={{display: "flex", justifyContent: "space-between", flexDirection: "column"}}>
                                <div>aaaa</div>
                                <div style={{color: "orange"}}>bbb</div>
                            </div>
                            <div>
                                <img style={{height: "60px"}} src="https://previews.123rf.com/images/aquir/aquir1311/aquir131100316/23569861-sample-grunge-red-round-stamp.jpg" />
                            </div>
                        </div>
                    </div>

                    ))}

                    </div>

                    <div>aaaaaaaaaaaaa</div>
                </div>
                {/* {showTable && (
                    <ReactTable
                        data={movies}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )} */}
            </Wrapper>
        )
    }
}

export default MoviesList
