import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
// import Button from 'react-bootstrap/Button'

import styled from 'styled-components'
import Item from '../components/Item'
// import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class MoviesList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            columns: [],
            isLoading: false,
            items: [],
            total: 40,
            subTotal: 0,
            displayItems: [],
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMovies().then(movies => {
            console.log(movies);
            this.setState({
                movies: movies.data.data,
                displayItems:  movies.data.data,
                isLoading: false,
            })
        })
    }

    addItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        console.log(itemUnitPrice);

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal + itemUnitPrice,
        })
    }

    removeItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        let newState = (this.state.items).map(obj =>
            obj.name === id ? { ...obj, quantity: obj.quantity-1, total: (obj.quantity-1)*itemUnitPrice } : obj
        );
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })
    }

    deleteItem(id) {
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;
        let newState = (this.state.items).filter((item) => item.name !== id);
        this.setState({
            items: newState,
            subTotal: this.state.subTotal - itemUnitPrice,
        })

        // console.log((this.state.items).sum('total'));
    }

    handleClick(id) {
        // this.setState(prevState => ({
        //     isToggleOn: !prevState.isToggleOn
        // }));
        console.log(this.state.items);
        let itemUnitPrice = (this.state.movies).filter(obj => obj.name === id)[0].price;

        // console.log((this.state.items).find(x => x.name === id))
        if ((this.state.items).find(x => x.name === id)) {
            console.log((this.state.items).find(x => x.name === id));

            let newState = (this.state.items).map(obj =>
                obj.name === id ? { ...obj, quantity: obj.quantity+1, total: (obj.quantity+1)*itemUnitPrice } : obj
            );

            console.log(newState)

            this.setState({
                items: newState
            })




        } else {
            console.log('aaaaaa');
            console.log(this.state.items);

            this.setState({
                items: [
                  ...this.state.items,
                  {'name': id, 'quantity' : 1, total: itemUnitPrice} 
                ]
              })



            console.log(this.state.subTotal);
        }

        this.setState({
            subTotal: this.state.subTotal + itemUnitPrice,
        })
        console.log('bbbbbbbbbbbbbbbbbbb');
        console.log(this.state.items);


    }

    addOrder() {
        console.log('orderrrrrrrrrrrr');
        //insertMovie
        api.insertMovie({"name" : "Chillie2",
        "time" : [ 
            "15:00", 
            "20:00"
        ],
        "total" : this.state.total}).then(order2 => {
            console.log("aaaadd");
            console.log(order2)

        })
    }

    changeCategory(category) {
        console.log(category)
        if (category == 'all') {
            this.setState({
                displayItems:  this.state.movies,
            })
        } else {
            let filteredItems = (this.state.movies).filter(obj => obj.name === 'Potatoes');
            this.setState({
                displayItems:  filteredItems,
            })
        }

    }

    clearOrder() {
        this.setState({
            items: [],
            total: 40,
            subTotal: 0,
        })
    }

    render() {
        const { movies, isLoading, items, displayItems } = this.state
        console.log('TCL: MoviesList -> render -> movies', movies)

        // const columns = [
        //     {
        //         Header: 'ID',
        //         accessor: '_id',
        //         filterable: true,
        //     },
        //     {
        //         Header: 'Name',
        //         accessor: 'name',
        //         filterable: true,
        //     },
        //     {
        //         Header: 'Rating',
        //         accessor: 'rating',
        //         filterable: true,
        //     },
        //     {
        //         Header: 'Time',
        //         accessor: 'time',
        //         Cell: props => <span>{props.value.join(' / ')}</span>,
        //     },
        // ]

        let showTable = true
        if (!movies.length) {
            showTable = false
        }

        return (
            <Wrapper>
                <div style={{marginTop: "10px", width: "50%"}}>
                    <h2>Simonss's BBQ Team</h2>
                    <h5>Simonss's BBQ Team</h5>
                </div>
                <div style={{display: "flex", justifyContent: "space-around", marginTop: "10px", width: "50%"}}>
                    <button style={{width: "83px", border: "none", background: "red", borderRadius: "12px", color: "white", padding: "6px"}} onClick={() => this.changeCategory('all')}>All</button>
                    <button style={{width: "83px", border: "none", background: "red", borderRadius: "12px", color: "white", padding: "6px"}} onClick={() => this.changeCategory('food')}>Food</button>
                </div>


                <div style={{display: "flex"}}>                    
                    <div style={{height: "100%", width: "60%", flexWrap: "wrap", display: "flex", justifyContent: "space-between"}} className="users">


                    <div style={{marginTop: "10px", width: "50%"}}>
                        <h2>Simonss's BBQ Team</h2>
                        <h5>Simonss's BBQ Team</h5>
                    </div>
                    <div style={{display: "flex", justifyContent: "space-around", marginTop: "10px", width: "50%"}}>
                        <button style={{width: "83px", border: "none", background: "red", borderRadius: "12px", color: "white", padding: "6px"}} onClick={() => this.changeCategory('all')}>All</button>
                        <button style={{width: "83px", border: "none", background: "red", borderRadius: "12px", color: "white", padding: "6px"}} onClick={() => this.changeCategory('food')}>Food</button>
                    </div>




