import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3003/api',
})

export const insertOrder = payload => api.post(`/pos`, payload)
export const getAllMovies = () => api.get(`/poss`)
export const getAllCategories = () => api.get(`/categories`)

const apis = {
    insertOrder,
    getAllMovies,
    getAllCategories
}

export default apis
