import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3003/api',
})

const api2 = axios.create({
    baseURL: 'https://api.imgur.com/3',
})

const headers = {
    'Authorization': 'Client-ID 1c5d597ec4cd5ac'
  }

export const insertOrder = payload => api.post(`/order`, payload)
export const getAllGoods = () => api.get(`/items`)
export const getAllCategories = () => api.get(`/categories`)
export const getAllConfigs = () => api.get(`/configs`)
export const getAllImages= () => api2.get(`/gallery/search/{{sort}}/{{window}}/{{page}}?q=tree`, {headers: headers})

const apis = {
    insertOrder,
    getAllGoods,
    getAllCategories,
    getAllConfigs
}

export default apis
